import React, { useState, useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'

export default function ViewOrders() {
    const [users, setUsers] = useState([])
    const [orders, setorders] = useState([])
    const [orderData, setorderData] = useState({})
    const [search, setSearch] = useState('')
    const navigate = useNavigate()
    const loadOrders = async () => {
        let response = await fetch("http://localhost:5000/api/auth/foodData", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: localStorage.getItem('userEmail')
            })
        }).then(async (res) => {
            let response = await res.json()
            await setUsers(response[2]);

        })
    }
    /*const loadOrders = async () => {
        let response = await fetch("http://localhost:5000/api/auth/mygyjOrderData", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: localStorage.getItem('userEmail')
            })
        }).then(async (res) => {
            let response = await res.json()
            await setorders(response)

        })
    }
    const fetchMyOrder = async (mail) => {
        await fetch("http://localhost:5000/api/auth/mygyjOrderData", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: localStorage.getItem(mail)
            })
        }).then(async (res) => {
            let response = await res.json()
            await setorderData(response)
        })
    }*/
    useEffect(() => {
        loadOrders()
            
    }, [])
    return (
        <>
            <div>
                <nav class="navbar navbar-light bg-light">
                    <div class="container">
                        <Link className="navbar-brand fs-1 fst-italic" to="/admin">GetYourFood</Link>
                        <form class="d-flex">
                            <input class="form-control me-2 border-secondary" type="search" placeholder="Search Order" aria-label="Search" value={search} onChange={(e) => { setSearch(e.target.value) }} />
                            <button class="btn btn-outline-secondary" onClick={() => { setSearch('') }}>Search</button>
                        </form>
                    </div>
                </nav>
            </div>

            
        </>
    )
}
