import React from 'react'
import { useEffect, useState } from 'react'
import Card from '../components/Card'
import Navbar from '../components/Navbar'
import { Link,useLocation } from "react-router-dom"
import Footer from '../components/Footer'
export default function CategoryExplore() {
    const [foodCat, setFoodCat] = useState("")
    const [foodItems, setFoodItems] = useState("")
    const [search, setSearch] = useState('')
    // const searchParams = new URLSearchParams(window.location.search);
    // const name = searchParams.get('catdata');
    //const catdata = props.location.state;
    //let foodCategory = props.Category;
    const { state } = useLocation();
    const { category } = state;

    const loadFoodItems = async () => {
        let response = await fetch("http://localhost:5000/api/auth/foodData", {
            // credentials: 'include',
            // Origin:"http://localhost:3000/login",
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            }

        });
        response = await response.json();
        //console.log(response[1][0].CategoryName);
        // console.log(response[1][1].CategoryName)
        setFoodItems(response[0])
        setFoodCat(category)
    }
    const [credentials, setCredentials] = useState({ CategoryName: "" })
    const onChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value })
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:5000/api/auth/updatecategory", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ CategoryName: credentials.foodCat,UpdatedCategoryName:credentials.UpdatedCategoryName })

        });
        const json = await response.json()
        console.log(json);
        if (json.success) {
            //save the auth toke to local storage and redirect
            //localStorage.setItem('token', json.authToken)
            alert("Category Updated Successfully")

        }
        else {
            alert("Not Updated")
        }
    }

    useEffect(() => {
        loadFoodItems()
    }, [])
    return (
        <>
            <div>  {/* justify-content-center, copy this <form> from navbar for search box */}
            <nav class="navbar navbar-light ">
                    <div class="container">
                        <Link className="navbar-brand fs-1 ms-2 text-secondary" to="/">{foodCat}</Link>
                        <form class="d-flex">
                            <input type="text" className="form-control me-2 border-secondary" name='UpdatedCategoryName' value={credentials.UpdatedCategoryName} onChange={onChange} placeholder='Update Category'/>
                            <button type="submit" className="btn btn-outline-secondary" onClick={handleSubmit}>Update</button>
                        </form>
                        <form class="d-flex">
                            <input class="form-control me-2 border-secondary" type="search" placeholder="Search Items" aria-label="Search" value={search} onChange={(e) => { setSearch(e.target.value) }} />
                            <button class="btn btn-outline-secondary" onClick={() => { setSearch('') }}>Search</button>
                        </form>
                    </div>
                </nav>
            </div>


            {/* <div class="d-grid gap-2">
                <div className="btn bg-white text-secondary mx-1 " to="/categoryexplore">{props.Category.CategoryName}</div>
            </div> */}
            <div className='container'> {/* boootstrap is mobile first */}
                
                <div className='row mb-3'>
                    {/*foodItems.length !== []*/}
                    {foodItems.length > 1 ? foodItems.filter(
                        (items) => (items.CategoryName === foodCat) && (items.name.toLowerCase().includes(search.toLowerCase())))
                        .map(filterItems => { 
                            return (
                                <div key={filterItems.id} className='col-12 col-md-6 col-lg-3'>
                                    {console.log(filterItems.url)}
                                    <Card foodName={filterItems.name} item={filterItems} price={filterItems.price} ImgSrc={filterItems.img} CategoryName={filterItems.CategoryName} id={filterItems._id} save={filterItems.Save} dealer={filterItems.Dealer} metal={filterItems.Metal} material={filterItems.Material} pricebreakup={filterItems.pricebreakup}></Card>
                        
                                </div>
                            )
                        }) : <div> No Such Data </div>}
                </div>

            </div>
            

        </>
    )
}
