import React,{ useState, useRef, useEffect } from 'react'
import Navbar from '../components/Navbar'
import { Link, useLocation } from "react-router-dom"
import Footer from '../components/Footer';
import { useNavigate } from 'react-router-dom'
import { useDispatchCart, useCart } from '../components/ContextReducer'
export default function ProductDetails() {
  const dispatch = useDispatchCart();
  const [qty, setQty] = useState(1)
  const [size, setSize] = useState("")
  let data = useCart();
  const location = useLocation();
  const product = location.state;
  let navigate = useNavigate()
  const handleQty = (e) => {
    setQty(e.target.value);
  }
  const handleAddToCart = async () => {
    if (!localStorage.getItem("token")) {
      navigate("/login")
    }
    let food = []
    for (const item of data) {
      if (item.id === product.id) {
        food = item;

        break;
      }
    }
    console.log(food)
    console.log(new Date())
    //food !== []
    if (food.length < 1) {
      if (food.size === size) {
        await dispatch({ type: "UPDATE", id: product.id, price: finalPrice, qty: qty })
        return
      }
      else if (food.size !== size) {
        await dispatch({ type: "ADD", id: product.id, name: product.name, price: finalPrice, qty: qty, size: size, img: product.img })
        console.log("Size different so simply ADD one more to the list")
        return
      }
      return
    }

    await dispatch({ type: "ADD", id: product.id, name: product.name, price: finalPrice, qty: qty })
  }
  const handleCheckOut = async () => {
    let userEmail = localStorage.getItem("userEmail");
    
    // console.log(data,localStorage.getItem("userEmail"),new Date())
    let response = await fetch("http://localhost:5000/api/auth/gyjorderData", {
      // credentials: 'include',
      // Origin:"http://localhost:3000/login",
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        order_data: data,
        email: userEmail,
        order_date: new Date().toDateString()
      })
    });
    console.log("JSON RESPONSE:::::", response.status)
    if (response.status === 200) {
      dispatch({ type: "DROP" })
    }
  }
  const saving = parseInt((product.save / 100) * product.price);
  const finalPrice = parseInt(product.price - (saving));

  return (
    <>
      <div>
        <Navbar />
      </div>

      <div className="container">
        <div className="row m-4">
          <div className="col-sm-5">
            <figure className="figure">
              <img src={product.img} className="figure-img img-fluid rounded" alt="..." />
            </figure>
          </div>
          <div className="col-sm-7 mt-4 text-secondary">
            <h2>{product.name}</h2>
            <p className='ms-2'><small>By</small> {product.dealer}<small> | Product Code: {product.id}</small></p>
            <hr />
            <small>Material used: {product.material} & {product.metal}</small>
            <h4 className='mt-4'>M.R.P : <small><s>₹ {product.price}</s></small> <b className='ms-3 fs-2'>₹{finalPrice}</b>
            <select className="ms-4 h-100 w-30 bg-outline-secondary text-secondary rounded" style={{ select: "#FF0000" }} onChange={handleQty}>
              {Array.from(Array(4), (e, i) => {
                return (
                  <option key={i + 1} value={i + 1}>{i + 1}</option>)
              })}
            </select>
            </h4>
            <div className='m-3'>
            
              <small>You Save <b className='text-warning'>₹ {saving}</b></small>
              <small className='border border-warning text-warning p-1 ms-5'>{product.save}% OFF</small>
            </div>
            <div className='m-2'>
              <p>View Price Breakup</p>
              <p><small className='border border-secondary text-secondary p-1'>Gold: {Object.values(product.pricebreakup[0])[0]}</small> + <small className='border border-secondary text-secondary p-1'>Diamond/Stone: {Object.values(product.pricebreakup[0])[1]}</small> + <small className='border border-secondary text-secondary p-1'>Making Charges: {Object.values(product.pricebreakup[0])[2]}</small> + <small className='border border-secondary text-secondary p-1'>GST (3%): {Object.values(product.pricebreakup[0])[3]}</small></p>
            </div>
            <hr />
            <div className='btn-group'>
            <button className={`btn btn-outline-secondary m-1`} onClick={handleAddToCart}>Add to Cart</button>
            <button className={`btn btn-outline-secondary m-1`} onClick={handleCheckOut}>Buy Now</button>
            </div>
          </div>
        </div>

      </div>
      <div>
        <Footer />
      </div>

    </>
  )
}
