import React, { useState, useRef, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { useDispatchCart, useCart } from './ContextReducer'
// import { Dropdown, DropdownButton } from 'react-bootstrap';

export default function Card(props) {
  let data = useCart();

  let navigate = useNavigate()
  const [qty, setQty] = useState(1)
  const [size, setSize] = useState("")
  const priceRef = useRef();
  // const [btnEnable, setBtnEnable] = useState(false);
  // let totval = 0
  // let price = Object.values(options).map((value) => {
  //   return parseInt(value, 10);
  // });
  let foodItem = props.item;
  const dispatch = useDispatchCart();
  const handleClick = () => {
    if (!localStorage.getItem("token")) {
      navigate("/login")
    }
  }
  const handleQty = (e) => {
    setQty(e.target.value);
  }

  const handleAddToCart = async () => {
    let food = []
    for (const item of data) {
      if (item.id === foodItem._id) {
        food = item;

        break;
      }
    }
    console.log(food)
    console.log(new Date())
    //food !== []
    if (food.length < 1) {
      if (food.size === size) {
        await dispatch({ type: "UPDATE", id: foodItem._id, price: finalPrice, qty: qty })
        return
      }
      else if (food.size !== size) {
        await dispatch({ type: "ADD", id: foodItem._id, name: foodItem.name, price: finalPrice, qty: qty, size: size, img: props.ImgSrc })
        console.log("Size different so simply ADD one more to the list")
        return
      }
      return
    }

    await dispatch({ type: "ADD", id: foodItem._id, name: foodItem.name, price: finalPrice, qty: qty, size: size })
  }

  let finalPrice = qty * parseInt(props.price);   //This is where Price is changing
  const product = { id: props.id, name: props.foodName, price: props.price, img: props.ImgSrc, category:props.CategoryName,
    save:props.save, dealer:props.dealer, metal:props.metal, material:props.material, pricebreakup:props.pricebreakup };
  return (
    <div>

      <div className="card mt-3" style={{ width: "17rem", maxHeight: "400px" }}>
        <img src={props.ImgSrc} className="card-img-top" alt="..." style={{ maxHeight: "180px", objectFit: "fill" }} />
        <div className="card-body">
          <h5 className="card-title text-secondary h6">{props.foodName}</h5>
          {/* <p className="card-text">This is some random text. This is description.</p> */}
          <div className='container w-100 p-0' style={{ height: "38px" }}>
            <select className="m-2 h-100 w-30 bg-outline-secondary text-secondary rounded" style={{ select: "#FF0000" }} onClick={handleClick} onChange={handleQty}>
              {Array.from(Array(4), (e, i) => {
                return (
                  <option key={i + 1} value={i + 1}>{i + 1}</option>)
              })}
            </select>
            <div className=' d-inline text-secondary ms-5 h-100 w-20 fs-5'>
              ₹{finalPrice}/-
            </div>
          </div>
          <hr></hr>
          
          <div className='btn-group'>
            <button className={`btn btn-outline-secondary m-1`} onClick={handleAddToCart}>Add to Cart</button>
            <button className={`btn btn-outline-secondary m-1`} onClick={() => navigate("/productdetails",{ state: product })}>View Details</button>
            </div>
            
          {/* <button className={`btn btn-danger justify-center ms-2 ${btnEnable ? "" : "disabled"}`} onClick={handleRemoveCart}>Remove</button> */}
        </div>
      </div>
    </div>
  )
}
