import React, { useState, useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useOrder, useDispatchOrder } from '../components/ContextReducerForAdmin';

export default function ViewOrders() {
    const [orders, setOrders] = useState({})
    const [search, setSearch] = useState('')
    let data = useOrder();
    let dispatch = useDispatchOrder();
    const navigate = useNavigate()
    const loadOrders = async () => {
        let response = await fetch("http://localhost:5000/api/auth/foodData", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: localStorage.getItem('userEmail')
            })
        }).then(async (res) => {
            let response = await res.json()
            await setOrders(response[3]);

        })
    }
    useEffect(() => {
        loadOrders()
    }, [])
    return (
        <>
            <div>
                <nav class="navbar navbar-light bg-light">
                    <div class="container">
                        <Link className="navbar-brand fs-1 fst-italic" to="/admin">GetYourFood</Link>
                        <form class="d-flex">
                            <input class="form-control me-2 border-secondary" type="search" placeholder="Search Order" aria-label="Search" value={search} onChange={(e) => { setSearch(e.target.value) }} />
                            <button class="btn btn-outline-secondary" onClick={() => { setSearch('') }}>Search</button>
                        </form>
                    </div>
                </nav>
            </div>
            <div className='mt-3'>
            <div className='m-auto mt-2 table-responsive  table-responsive-sm table-responsive-md' >
          <table className='table table-hover '>
            <thead className=' text-secondary fs-4'>
              <tr className='bg-white'>
                <th scope='col' >Product_id</th>
                <th scope='col' >Product Name</th>
                <th scope='col' >Quantity</th>
                <th scope='col' >Amount</th>
                <th scope='col' >Delete</th>
              </tr>
            </thead>
            <tbody>
              {data.map((food, index) => (
                <tr>
                  <th scope='row' >{index + 1}</th>
                  <td >{food.name}</td>
                  <td>{food.qty}</td>
                  <td>{food.price}</td>
                  
                </tr>
              ))}
            </tbody>
          </table>
        
          
        </div>
            </div>

        </>
    )
}

