import React, { useState } from 'react'
import AdminNavbar from '../components/AdminNavbar'
import Footer from '../components/Footer'

export default function AddItem() {
    const [credentials, setCredentials] = useState({ CategoryName: "", name: "", price: "", description: "" })
    const onChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value })
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:5000/api/auth/additem", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                CategoryName: credentials.CategoryName, name: credentials.name,
                img: credentials.img, price: credentials.price, Metal: credentials.Metal, Material: credentials.Material,
                Save: credentials.Save,
                dealer: credentials.dealer,
                pricebreakup: [{
                    'Gold': "YourGoldValue",
                    'Diamond/Stone': "YourDiamondStoneValue",
                    'Making Charges': "YourMakingChargesValue",
                    'GST(3%)': "YourGSTValue"
                }]
            })

        });
        const json = await response.json()
        console.log(json);
        if (json.success) {
            //save the auth toke to local storage and redirect
            //localStorage.setItem('token', json.authToken)
            alert("Item Added Successfully")

        }
        else {
            alert("Item Not Added")
        }
    }

    return (
        <>
            <div className='mb-5'>
                <AdminNavbar />
            </div>
            <div className='container' >
                <form className='w-50 m-auto mt-5 border text-white border-secondary rounded' style={{ backgroundColor: "#fff" }} onSubmit={handleSubmit}>
                    <div className="m-3">
                        <input type="text" className="form-control" name='CategoryName' value={credentials.CategoryName} onChange={onChange} placeholder="Enter Category Name" aria-describedby="emailHelp" />
                    </div>
                    <div className="m-3">
                        <input type="text" className="form-control" name='name' value={credentials.name} onChange={onChange} placeholder="Enter Item Name" />
                    </div>
                    <div className="m-3">
                        <input type="text" className="form-control" name='img' value={credentials.img} onChange={onChange} placeholder="Enter Image Link" />
                    </div>
                    <div className="m-3">
                        <input type="text" className="form-control" name='description' placeholder="Enter Item Description" />
                    </div>
                    <div className="m-3">
                        <input type="text" className='form-control' name="price" value={credentials.price} onChange={onChange} placeholder="Enter Price" />
                    </div>
                    <div className='text-center'>
                        <button type="submit" className="m-3 btn btn-secondary text-white">Add Item</button>
                    </div>
                </form>
            </div>
            <div className='mt-5'>
                <Footer />
            </div>
        </>
    )
}
