import React from 'react'
import Navbar from '../components/Navbar'
import { Link, useLocation } from "react-router-dom"
import Footer from '../components/Footer';

export default function ProductDetails() {
  const location = useLocation();
  const product = location.state;
  const finalPrice=parseInt(product.price-((product.save/100)*product.price));
  
  return (
    <>
      <div>
        <Navbar />
      </div>

      <div className="container">
        <div className="row m-4">
          <div className="col-sm-5">
            <figure className="figure">
              <img src={product.img} className="figure-img img-fluid rounded" alt="..." />
            </figure>
          </div>
          <div className="col-sm-7 mt-4 text-secondary">
            <h2>{product.name}</h2>
            <p className='ms-2'><small>By</small> {product.dealer}<small> | Product Code: {product.id}</small></p>
            <hr />
            <small>Material used: {product.material} & {product.metal}</small>
            <h4 className='mt-4'>M.R.P : <small><s>₹ {product.price}</s></small> <b className='fs-2'>₹{finalPrice}</b></h4>
          </div>
        </div>

      </div>
      <div>
        <Footer />
      </div>

    </>
  )
}
/*
{product.id}
{product.name}
{product.price}
{product.metal}
{Object.values(product.pricebreakup[0])[0]}
*/
