import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Badge from '@material-ui/core/Badge';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { useCart } from './ContextReducer';
import Modal from '../Modal';
import Cart from '../screens/Cart';
import './Navbar.css';

export default function Navbar(props) {
  const [cartView, setCartView] = useState(false);
  const [isNavCollapsed, setIsNavCollapsed] = useState(true);
  let navigate = useNavigate();

  const handleToggleNav = () => {
    setIsNavCollapsed(!isNavCollapsed);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const loadCart = () => {
    setCartView(true);
  };

  const items = useCart();

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark bg-secondary position-sticky">
        <div className="container-fluid">
          <Link className="navbar-brand fs-1 fst-italic" to="/">
            GetYourJewellery
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded={!isNavCollapsed ? true : false}
            aria-label="Toggle navigation"
            onClick={handleToggleNav}
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div
            className={`${isNavCollapsed ? 'collapse' : ''} navbar-collapse`}
            id="navbarSupportedContent"
          >
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              {/* ... Your existing menu items ... */}
            </ul>
            {!localStorage.getItem('token') ? (
              <form className="d-flex">
                <Link className="btn bg-white text-secondary mx-1" to="/login">
                  Login
                </Link>
                <Link className="btn bg-white text-secondary mx-1" to="/signup">
                  Signup
                </Link>
              </form>
            ) : (
              <div>
                {/* ... Your existing logged-in content ... */}
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
}
