import React, { useState } from 'react'
import AdminNavbar from '../components/AdminNavbar'
import Footer from '../components/Footer'
const MobileNav = styled.ul`
  display: none;
  flex-direction: column;
  margin: 0;
  padding: 0;

  @media (max-width: 767px) {
    display: flex;
  }
`;
export default function AddCategory() {
    const [credentials, setCredentials] = useState({ CategoryName: "" })
    const onChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value })
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:5000/api/auth/addcategory", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ CategoryName: credentials.CategoryName })

        });
        const json = await response.json()
        console.log(json);
        if (json.success) {
            //save the auth toke to local storage and redirect
            //localStorage.setItem('token', json.authToken)
            alert("Category Added Successfully")

        }
        else {
            alert("Not Added")
        }
    }
    return (
        <>
            <div className='mb-5'>
                <AdminNavbar />
            </div>
            <div className='container'>
                <form className='w-50 m-auto mt-5 border text-white border-secondary rounded' style={{ backgroundColor: "#fff" }} onSubmit={handleSubmit}>
                    <div className="m-3">
                        <label htmlFor="exampleInputEmail1" className="form-label">Category Name</label>
                        <input type="text" className="form-control" name='CategoryName' value={credentials.CategoryName} onChange={onChange} placeholder="Enter Category Name" />
                    </div>
                    <div className='text-center'>
                        <button type="submit" className="m-3 btn btn-secondary text-white">Add Category</button>
                    </div>
                </form>
            </div>
            <MobileNav style={{ display: isMobileNavOpen ? 'flex' : 'none' }}>

                {(!localStorage.getItem("token")) ?
                    <>
                        <NavItem><Link to="/login">Login</Link></NavItem>
                        <NavItem><Link to="/signup">Signup</Link></NavItem>
                    </> :
                    <NavItem><Link onClick={handleLogout}>Logout</Link></NavItem>
                }
            </MobileNav>
            <div className='mt-5'>
                <Footer />
            </div>
        </>
    )
}
